mapper.select:
select id, name, `desc`, tag, total, discount, min, `limit`, type, status, goods_type, goods_value, code, time_type, days, start_time, end_time, add_time, update_time, deleted from cskaoyan_mall_coupon
type = #{type,jdbcType=SMALLINT}
and status = #{status,jdbcType=SMALLINT}
and name like #{name}
order by #{sort} #{order}

select id, user_id, Coupon_id, status, used_time, start_time, end_time, order_id, add_time, update_time, deleted from cskaoyan_mall_coupon_user
user_id = #{userId,jdbcType=INTEGER}
and status = #{status,jdbcType=SMALLINT}
and coupon_id = #{couponId,jdbcType=INTEGER}
order by #{sort} #{order}
