mapper.select:
select `id`, `name`, `desc` , `enabled`, `add_time`, `update_time`, `deleted` from cskaoyan_mall_role where deleted = 0
and `name` like CONCAT('%',#{name},'%')
order by #{sort} #{order}

mapper.update:
update cskaoyan_mall_role
set `name` = #{role.name}, `desc` = #{role.desc}, update_time = now()
where id = #{role.id}
