mapper.insert:
insert into cskaoyan_mall_goods_product (id, goods_id, specifications,
price, `number`, url, add_time, update_time
)
values

    (null,#{goodsId},#{item.specifications ,typeHandler = com.cskaoyan.mall.typeHandler.StringToStringArrary}
    ,#{item.price},#{item.number},#{item.url},now(),now())

mapper.select:
select id, goods_sn, name, category_id, brand_id, gallery, keywords, brief, is_on_sale, sort_order, pic_url, share_url, is_new, is_hot, unit, counter_price, retail_price, add_time, update_time, deleted from cskaoyan_mall_goods
where goods_sn = #{goodsSn}
where `name` like concat('%',concat(#{name}),'%')
order by #{sort} #{order}
