mapper.select:
select category_id
from cskaoyan_mall_goods
where id = #{id}

select id, goods_sn, name, category_id, brand_id, gallery, keywords, brief, is_on_sale, sort_order, pic_url, share_url, is_new, is_hot, unit, counter_price, retail_price, add_time, update_time, deleted from cskaoyan_mall_goods where deleted = 0
and goods_sn = #{goodsSn}
and `name` like concat('%',concat(#{name}),'%')
order by #{sort} #{order}

